import{_ as t,o as e,c as a,Q as i}from"./chunks/framework.544e8ca7.js";const h=JSON.parse('{"title":"浏览器加载流程","description":"从输入 URL 到页面加载完成，发生了什么","frontmatter":{"title":"浏览器加载流程","description":"从输入 URL 到页面加载完成，发生了什么","date":"2019-12-20T00:00:00.000Z","author":"Ruiyoung","tag":["前端性能优化","浏览器"]},"headers":[],"relativePath":"FrontendPerformanceOptimization/2019-12-20-浏览器加载流程.md","lastUpdated":null}'),o={name:"FrontendPerformanceOptimization/2019-12-20-浏览器加载流程.md"},r=i('<h4 id="从输入-url-到页面加载完成-发生了什么" tabindex="-1">从输入 URL 到页面加载完成，发生了什么？ <a class="header-anchor" href="#从输入-url-到页面加载完成-发生了什么" aria-label="Permalink to &quot;从输入 URL 到页面加载完成，发生了什么？&quot;">​</a></h4><p>首先我们需要通过 DNS（域名解析系统）将 URL 解析为对应的 IP 地址，然后与这个 IP 地址确定的那台服务器建立起 TCP 网络连接，随后我们向服务端抛出我们的 HTTP 请求，服务端处理完我们的请求之后，把目标数据放在 HTTP 响应里返回给客户端，拿到响应数据的浏览器就可以开始走一个渲染的流程。渲染完毕，页面便呈现给了用户，并时刻等待响应用户的操作</p><h4 id="我们将这个过程切分为如下的过程片段" tabindex="-1">我们将这个过程切分为如下的过程片段： <a class="header-anchor" href="#我们将这个过程切分为如下的过程片段" aria-label="Permalink to &quot;我们将这个过程切分为如下的过程片段：&quot;">​</a></h4><ul><li>DNS 解析</li><li>TCP 连接</li><li>HTTP 请求抛出</li><li>服务端处理请求，HTTP 响应返回</li><li>浏览器拿到响应数据，解析响应内容，把解析的结果展示给用户</li></ul>',4),_=[r];function n(l,s,c,d,p,u){return e(),a("div",null,_)}const P=t(o,[["render",n]]);export{h as __pageData,P as default};
