import{_ as o,o as a,c as n,A as e,a as t}from"./chunks/framework.544e8ca7.js";const b=JSON.parse('{"title":"CDN 的缓存与回源机制解析","description":"从CDN着手性能优化","frontmatter":{"title":"CDN 的缓存与回源机制解析","description":"从CDN着手性能优化","date":"2019-12-21T00:00:00.000Z","author":"Ruiyoung","tag":["前端性能优化","浏览器"]},"headers":[],"relativePath":"FrontendPerformanceOptimization/2019-12-21-CDN 的缓存与回源机制解析.md","lastUpdated":null}'),r={name:"FrontendPerformanceOptimization/2019-12-21-CDN 的缓存与回源机制解析.md"},s=e("blockquote",null,[e("blockquote",null,[e("p",null,"CDN （Content Delivery Network，即内容分发网络）指的是一组分布在各个地区的服务器。这些服务器存储着数据的副本，因此服务器可以根据哪些服务器与用户距离最近，来满足数据的请求。 CDN 提供快速服务，较少受高流量影响。")])],-1),c=e("h2",{id:"cdn-的核心点有两个-一个是缓存-一个是回源。",tabindex:"-1"},[t("CDN 的核心点有两个，一个是缓存，一个是回源。 "),e("a",{class:"header-anchor",href:"#cdn-的核心点有两个-一个是缓存-一个是回源。","aria-label":'Permalink to "CDN 的核心点有两个，一个是缓存，一个是回源。"'},"​")],-1),i=e("p",null,"这两个概念都非常好理解。对标到上面描述的过程，“缓存”就是说我们把资源 copy 一份到 CDN 服务器上这个过程，“回源”就是说 CDN 发现自己没有这个资源（一般是缓存的数据过期了），转头向根服务器（或者它的上层服务器）去要这个资源的过程。",-1),_=e("h3",{id:"cdn-往往被用来存放静态资源。",tabindex:"-1"},[t("CDN 往往被用来存放静态资源。 "),e("a",{class:"header-anchor",href:"#cdn-往往被用来存放静态资源。","aria-label":'Permalink to "CDN 往往被用来存放静态资源。"'},"​")],-1),l=e("p",null,"所谓“静态资源”，就是像 JS、CSS、图片等不需要业务服务器进行计算即得的资源。",-1),d=e("p",null,"同一个域名下的请求会不分青红皂白地携带 Cookie，而静态资源往往并不需要 Cookie 携带什么认证信息。把静态资源和主页面置于不同的域名下，完美地避免了不必要的 Cookie 的出现！所以 CDN 域名选取不要和 HTTP 请求域名一致",-1),C=[s,c,i,_,l,d];function p(N,h,D,u,m,f){return a(),n("div",null,C)}const x=o(r,[["render",p]]);export{b as __pageData,x as default};
